import axios from "axios";
import { API_LEARNING_GET } from "../config/api";

export const getLearningAlutsistaRequest = async ({ filter }) => {
    // const user = getLocalUser();
    try {
        const response = await axios.get(`${API_LEARNING_GET}/alutsista?${filter}`, {
            headers: {
                Authorization: `bearer eyJ0eXAiOiJKV1QiLCJhbGciOiJIUzI1NiJ9..NmhGNPK-EpJfPUZMxlhl8JUiB2u4_y9K5ozPiM3uxl4`,
            },
        });
        return response.data.list_data.learning;
    } catch (error) {
        console.error("Error fetching items:", error);
    }
};

